mapper.select:
SELECT result2.*
FROM   (SELECT result1.*, ROWNUM rnum
		FROM   (SELECT DISTINCT 		
				FROM   v_art 
				
				
				) result1
		) result2
WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE t_art 
SET    title=#{title}
	,  createyear=#{createyear}
	,  artsize=#{artsize}
	,  imagepath=#{imagepath}
	,  remark=#{remark}
WHERE seq=#{seq}
