mapper.update:
UPDATE T_Category set name=#{name}
     , remark=#{remark}
     , imagePath=#{imagePath}
     , bdate=#{bdate}
     , ddate=#{ddate}
 WHERE seq=#{seq}

mapper.select:
SELECT 
  FROM T_Category
 WHERE code='A' AND num=#{num}

SELECT 
  FROM T_Category
 WHERE code='M' AND num=#{num}

SELECT 
  FROM T_Category
 WHERE code='P' AND num=#{num}
