mapper.select:
SELECT id
FROM   t_favorArt
WHERE  id=#{id} AND aseq=#{aseq}

SELECT * FROM ( SELECT a.*, LAG(seq,1,0) OVER (ORDER BY id, a.regdate DESC) AS prevNum, LEAD(seq,1,0) OVER (ORDER BY id, a.regdate DESC) AS nextNum FROM t_favorArt a, t_art b WHERE a.aseq = b.seq
AND id=#{id}
ORDER BY id, a.regdate DESC ) WHERE seq=#{aseq}

SELECT b.*, a.*
FROM   t_favorArt a, t_art b
WHERE  a.aseq = b.seq

	AND id=#{id}	


	AND aseq=#{aseq}	

ORDER BY id, a.regdate DESC

SELECT MAX(ROWNUM)
FROM   t_favorArt

	
		AND id=#{id}	
	
	
		AND aseq=#{aseq}

mapper.delete:
DELETE
FROM   t_favorArt
WHERE  id=#{id} AND aseq=#{aseq}
