mapper.select:
SELECT result2.*, ( select remark from t_art where seq=#{seq}) AS remark FROM ( SELECT result1.*, LAG(seq,1,0) OVER ( ) AS prevNum, LEAD(seq,1,0) OVER ( ) AS nextNum FROM ( SELECT DISTINCT TITLE, SEQ, CREATEYEAR, ARTSIZE, IMAGEPATH, VIEWCOUNT, REGDATE, ARTIST, PAINTER, MATERIAL FROM V_ART
) result1
) result2 WHERE seq=#{seq}
