mapper.select:
SELECT id 
FROM T_User 
WHERE id=#{id}

		SELECT id, pwd, email, roll, regdate, deldate 
  		FROM T_User 
  		WHERE id=#{id}

mapper.update:
UPDATE T_User 
SET id=#{id}, email=#{email}, roll=#{roll}, deldate=#{deldate}
WHERE id=#{id}

UPDATE T_User 
SET pwd=#{pwd} 
WHERE id=#{id}

UPDATE T_User
SET deldate=SYSDATE
WHERE id=#{id}
